describe('Checking the indentation value of list after applying the decrease indent', () => {
    let container: DocumentEditor;
    beforeAll(() => {
        document.body.innerHTML = '';
        let ele: HTMLElement = createElement('div', { id: 'container' });
        document.body.appendChild(ele);
        DocumentEditor.Inject(Editor, Selection, EditorHistory, SfdtExport);
        container = new DocumentEditor({ enableEditor: true, isReadOnly: false, enableEditorHistory: true, enableSfdtExport: true });
        (container.documentHelper as any).containerCanvasIn = TestHelper.containerCanvas;
        (container.documentHelper as any).selectionCanvasIn = TestHelper.selectionCanvas;
        (container.documentHelper.render as any).pageCanvasIn = TestHelper.pageCanvas;
        (container.documentHelper.render as any).selectionCanvasIn = TestHelper.pageSelectionCanvas;
        container.appendTo('#container');
    });
    afterAll((done): void => {
        container.destroy();
        document.body.removeChild(document.getElementById('container'));
        container = undefined;
        document.body.innerHTML = '';
        setTimeout(function () {
            done();
        }, 1000);
    });

    it('To check left indent value of list after decrease indent',() =>{
        console.log('To check left indent value of list after decrease indent');
        container.openBlank();
        container.open(sfdt);
        container.selection.selectAll();
        container.editor.decreaseIndent();
        let bodyWidget: BodyWidget = container.editor.documentHelper.pages[0].bodyWidgets[0] as BodyWidget;
        let paragraphWidget: ParagraphWidget = bodyWidget.childWidgets[0] as ParagraphWidget;
        expect(paragraphWidget.paragraphFormat.leftIndent).toBe(18);
    });
});

